// TODO: iOS 13.4 upgrade broke this
//https://github.com/richtr/NoSleep.js/
//https://w3c.github.io/screen-wake-lock/ April 2020

// This solution worked for me -- I haven't implemented it in the context of nosleep.js,
// but this is the technique, in case anyone would like to update the library to fix this problem:
// Play a video that has an audio track. Make sure it's in the DOM and on-screen. (Covered by something else is fine.)

// Set up a callback when the video is paused -- this will trigger when the video reaches its end.
// When it's triggered, set the time to 0 and call play() on the video again.

// Where that went wrong -- after a few minutes, the playhead would stop advancing; it would just stay at 0.
// If you asked the video player what it was up to, it would say that it was playing, not paused, not completed,
//  not seeking, no errors. But its current time was always 0. And then the phone would fall asleep.

// So, every few moments (as long as "every few moments" is not an even multiple or divisor of the length of the video),
// check to see what the video's current time is, and store that time for the next check. If the video's current time is the same as

// what it was during the previous check, nudge the playhead forward slightly and call play() on the video again.

// Source: https://github.com/richtr/NoSleep.js/blob/master/src/media.js
const mp4 =
    "data:video/mp4;base64,";

// Source: https://github.com/richtr/NoSleep.js/blob/master/src/index.js
// Detect iOS browsers < version 10
const oldIOS =
    typeof navigator !== "undefined" &&
    parseFloat(
        ("" + (/CPU.*OS ([0-9_]{3,4})[0-9_]{0,1}|(CPU like).*AppleWebKit.*Mobile/i.exec(navigator.userAgent) || [0, ""])[1])
            .replace("undefined", "3_2")
            .replace("_", ".")
            .replace("_", "")
    ) < 10 &&
    !window.MSStream;

class NoSleep {
    constructor() {
        if (!Platform.is_iOS || oldIOS) return; // only iOS safari needs this 2020May20

        console.log("starting no sleep");
        if (oldIOS) {
            console.log("old");
            this.noSleepTimer = null;
        } else {
            this.lastCurrentTime = null;

            // Set up no sleep video element
            this.noSleepVideo = document.createElement("video");
            document.body.appendChild(this.noSleepVideo);
            this.noSleepVideo.style.display = "none";
            // title is not used because it should not ever show -> we delete the video element from the dom when tab is hidden.
            this.noSleepVideo.setAttribute("title", `Resume ${"cast"} in browser.`);
            this.noSleepVideo.setAttribute("playsinline", "");

            this._addSourceToVideo(this.noSleepVideo, "mp4", mp4);
            this.handleBrowserState(this.noSleepVideo, this);

            let self = this;

            this.pauseEventListener = function (event) {
                self.noSleepVideo.currentTime = 0;
                self.noSleepVideo.play();
            };
            this.noSleepVideo.addEventListener("pause", this.pauseEventListener, true);
        }
    }

    _addSourceToVideo(element, type, dataURI) {
        var source = document.createElement("source");
        source.src = dataURI;
        source.type = `video/${type}`;
        element.appendChild(source);
    }

    handleBrowserState(videoElement, self) {
        // Set the name of the hidden property and the change event for visibility
        var hidden, visibilityChange;
        if (typeof document.hidden !== "undefined") {
            // Opera 12.10 and Firefox 18 and later support
            hidden = "hidden";
            visibilityChange = "visibilitychange";
        } else if (typeof document.msHidden !== "undefined") {
            hidden = "msHidden";
            visibilityChange = "msvisibilitychange";
        } else if (typeof document.webkitHidden !== "undefined") {
            hidden = "webkitHidden";
            visibilityChange = "webkitvisibilitychange";
        }

        function win_focusChange(evt) {
            console.log("noSleep dispathing win_focusChange");
            if (evt && evt.target !== window) return;
            if (!document.hasFocus()) {
                console.log("noSleep !document.hasFocus()");
                self.disable();
            }
        }

        window.addEventListener("focus", win_focusChange, true);
        window.addEventListener("blur", win_focusChange, true);
    }

    enable() {
        if (!Platform.is_iOS || oldIOS) return; // only iOS safari needs this 2020May20

        if (oldIOS) {
            this.disable();
            // console.warn(`
            //     NoSleep enabled for older iOS devices. This can interrupt
            //     active or long-running network requests from completing successfully.
            //     See https://github.com/richtr/NoSleep.js/issues/15 for more details.
            // `);
            this.noSleepTimer = window.setInterval(() => {
                if (!document.hidden) {
                    window.location.href = window.location.href.split("#")[0];
                    window.setTimeout(window.stop, 0);
                }
            }, 15000);
        } else {
            this.noSleepVideo.play();
            let self = this;
            this.noSleepTimer2 = window.setInterval(() => {
                if (!document.hasFocus()) {
                    self.disable();
                } else {
                    let currentVideoTime = self.noSleepVideo.currentTime;
                    if (currentVideoTime === self.lastCurrentTime) {
                        self.noSleepVideo.currentTime += 0.01;
                        self.noSleepVideo.play();
                    }
                    self.lastCurrentTime = currentVideoTime;
                }
            }, self.noSleepVideo.duration * 1.11);
        }
    }

    disable() {
        if (!Platform.is_iOS || oldIOS) return; // only iOS safari needs this 2020May20

        if (oldIOS) {
            if (this.noSleepTimer) {
                // console.warn(`NoSleep now disabled for older iOS devices.`);
                window.clearInterval(this.noSleepTimer);
                this.noSleepTimer = null;
            }
        } else {
            if (document.body.contains(this.noSleepVideo)) {
                document.body.removeChild(this.noSleepVideo);
            }
            window.clearInterval(this.noSleepTimer2);
            this.noSleepTimer2 = null;
            this.noSleepVideo.loop = false;
            this.noSleepVideo.removeEventListener("pause", this.pauseEventListener, true);
        }
    }
}

let noSleep = new NoSleep();
document.addEventListener("click", function enableNoSleep() {
    noSleep.enable();
});
